sys_variable_value.value:
/**
 * Mask String Characters
 * 
 * This script masks characters in a given string from a specified start index to an end index (exclusive) with asterisks.
 * 
 * Inputs:
 * - OriginalString: The input string to be masked (string).
 * - StartIndex: The 0-based starting index for masking (integer).
 * - EndIndex: The exclusive ending index for masking (integer).
 * 
 * Outputs:
 * - result_string: The masked string (string).
 * 
 * Logic:
 * - Checks for empty string and returns it unchanged.
 * - Validates indices: ensures 0 <= StartIndex < EndIndex <= OriginalString.length.
 * - If indices are invalid, returns the original string unchanged.
 * - Otherwise, constructs the masked string by concatenating the unmasked prefix, asterisks for the masked portion, and the unmasked suffix.
 * 
 * Example:
 * - OriginalString: "testing", StartIndex: 2, EndIndex: 4
 * - Result: "te**ing"
 */
(function execute(inputs, outputs) {
    // Validate required inputs
    if (!inputs.OriginalString || typeof inputs.OriginalString !== 'string') {
        gs.error('Mask String Error: OriginalString must be a provided string');
        outputs.result_string = '';
        return;
    }
    
    var original = inputs.OriginalString;
    var start = inputs.StartIndex;
    var end = inputs.EndIndex;
    
    // Handle empty string
    if (original.length === 0) {
        outputs.result_string = original;
        return;
    }
    
    // Validate indices
    if (start < 0 || end > original.length || start >= end) {
        gs.error('Mask String Error: Invalid indices provided, returning original string');
        outputs.result_string = original;
        return;
    }
    
    // Construct masked string
    var prefix = original.substring(0, start);
    var maskLength = end - start;
    var mask = '*'.repeat(maskLength);
    var suffix = original.substring(end);
    
    outputs.result_string = prefix + mask + suffix;
})(inputs, outputs);

